import React from 'react';
import './ApiCall.css';

class Source extends React.Component {
    constructor() {
        super();
        this.state = {
            country: [],
            confirmed: [],
            deaths: [],
            isLoading: true,
        };
    }

    componentDidMount() {
        this.loadCommitHistory();
    }

    loadCommitHistory() {
        // Fetching data from FaceBook Jest Repo
        const key = 'da328055e2e940d8b28055e2e9e0d851';
        fetch(
            'https://api.weather.com/v3/wx/disease/tracker/countryList/current?format=json&language=en-US&apiKey=da328055e2e940d8b28055e2e9e0d851',
            {
                method: 'GET',
                headers: new Headers({
                    accept: 'application/json',
                }),
            }
        )
            .then((res) => res.json())
            .then((response) =>
                this.setState({
                    country: response.covid19.recordLocation,
                    confirmed: response.covid19.confirmed,
                    deaths: response.covid19.deaths,
                    isLoading: false,
                })
            )
            .catch((error) => console.log(error));
    }

    render() {
        const { country, confirmed, deaths, isLoading } = this.state;
        return (
            <div class="container">
                <h1 class="title"> COVID-19 Dashboard </h1>

                {isLoading && (
                    <div class="loaderWrapper">
                        <div class="loader"></div>
                    </div>
                )}

                <div class="countriesWrapper">
                    {country.map((c, index) => (
                        <div class="country">
                            <p class="countryName">{c}</p>
                            <div class="info">
                                <div class="infoItem confirmed">
                                    <span>Confirmed</span>
                                    <span>
                                        {confirmed[index] == null
                                            ? 0
                                            : confirmed[index]}
                                    </span>
                                </div>
                                <div class="infoItem deaths">
                                    <span>Deaths</span>
                                    <span>
                                        {deaths[index] == null
                                            ? 0
                                            : deaths[index]}
                                    </span>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    }
}

export default Source;
