import React from "react";

//import "./styles.css";

class Source extends React.Component {
  constructor() {
    super();
    this.state = {
      commitHistory: {},
      isLoading: true
    };
  }

  componentDidMount() {
    this.loadCommitHistory();
  }

  loadCommitHistory() {
    // Fetching data from FaceBook Jest Repo
    // const key='da328055e2e940d8b28055e2e9e0d851';
    fetch(
        'https://api.weather.com/v3/wx/disease/tracker/countryList/current?format=json&language=en-US&apiKey=da328055e2e940d8b28055e2e9e0d851',
      {
        method: "GET",
        headers: new Headers({
            accept: "application/json"
        })
      }
    )
      .then(res => res.json())
      .then(response =>
        this.setState({ commitHistory: response, isLoading: false })
      )
      .catch(error => console.log(error));
  }

  render() {
    const { commitHistory, isLoading } = this.state;
    const countries = isLoading?[]: commitHistory.covid19.recordLocation;
    const confirmed =isLoading?[]: commitHistory.covid19.confirmed;
    const deaths =isLoading?[]: commitHistory.covid19.deaths;
    return (
        <div>
            <h1 class="Heading"> COVID19 cases accross world</h1>
            {isLoading && <p>Wait I'm Loading comments for you</p>}
            {!isLoading && <div><p>COUNTRY  -  CONFIRMED-   DEATHS</p></div>}
            {countries.map((country, index) => (
                   <> <div>
                        <p>
                            {country} - {confirmed[index]} -{deaths[index]}
                        </p>
                    </div>
                    <hr/></>
            ))}
        </div>
    );
  }
}

export default Source;
